var OUT_SELECTED = RED.util.cloneMessage(global.get(msg.topic)||{});
if (OUT_SELECTED === undefined){
    msg.payload = "Error";

}
else{

    if(msg.topic === "OUT6")
    {
        if(OUT_SELECTED.CMD === true)
        {
            OUT_SELECTED.CMD = msg.payload;
            OUT_SELECTED.STATE = false;
            global.set((msg.topic),OUT_SELECTED);
            msg.payload = OUT_SELECTED;
            global.set( "REQUEST_UPDATE", true);
            return msg;
        }

        else if(OUT_SELECTED.CMD === false)
        {
            OUT_SELECTED.CMD = msg.payload;
            OUT_SELECTED.STATE = true;
            global.set((msg.topic),OUT_SELECTED);
            msg.payload = OUT_SELECTED;
            global.set( "REQUEST_UPDATE", true);
            return msg;
        }
    }

    else
    {
        OUT_SELECTED.CMD = msg.payload;
        global.set((msg.topic),OUT_SELECTED);
        msg.payload = OUT_SELECTED;
        global.set( "REQUEST_UPDATE", true);
        return msg;
    }
}

switch(msg.topic)
{
    case "ubutton":
        update_sts=flow.get("update_sts");

        if(1 == update_sts)
        {
            msg.payload = 2;
        }
        else
        {
            msg.payload = 0;
        }

        break;
/*        
    case "gcp/new_sw":
        global.set("update_sts", msg.payload.value);
        break;
*/        
    default:
        msg.payload=0;
        break;
}
return msg;

switch(msg.topic)
{
    case "gcp/new_sw":
        flow.set("update_sts", msg.payload.value);
        flow.set("new_sw_version", msg.payload.new_sw_version);
        if (msg.payload.value)
        {
            msg.payload.label="New SW available: ";
        }
        else
        {
            msg.payload.label="";
        }
