// Important
// It s impossible to manipulate global.structure whithout cloned data
// Please don't optimize this code

var local_msg = RED.util.cloneMessage(msg);
var msg1 = null;
var msg2 = null;
var msg3 = null;
var msg4 = null;

if (local_msg.payload.canid === 0x30A){
    // Local Cloning of the information
    var LOCAL_OUTA = RED.util.cloneMessage(global.get('OUT27'));
    var LOCAL_OUTB = RED.util.cloneMessage(global.get('OUT28'));
    var LOCAL_OUTC = RED.util.cloneMessage(global.get('OUT29'));
    var LOCAL_OUTD = RED.util.cloneMessage(global.get('OUT30'));

    // Extraction of the Data from the CAN FRAME
    // can be taken as a 16 bits value (12 bit ADC)
    LOCAL_OUTA.I_RAW_ADC = (((local_msg.payload.data[1]<<8)+local_msg.payload.data[0])).toFixed(0);
    LOCAL_OUTB.I_RAW_ADC = (((local_msg.payload.data[3]<<8)+local_msg.payload.data[2])).toFixed(0);
    LOCAL_OUTC.I_RAW_ADC = (((local_msg.payload.data[5]<<8)+local_msg.payload.data[4])).toFixed(0);
    LOCAL_OUTD.I_RAW_ADC = (((local_msg.payload.data[7]<<8)+local_msg.payload.data[6])).toFixed(0);
    // Convert DAC to mA 
    LOCAL_OUTA.I_AVG = ((((local_msg.payload.data[1]<<8)+local_msg.payload.data[0])*LOCAL_OUTA.CONF_CAL_CURRENT_COEF_A+LOCAL_OUTA.CONF_CAL_CURRENT_COEF_B)/1000).toFixed(1);
    LOCAL_OUTB.I_AVG = ((((local_msg.payload.data[3]<<8)+local_msg.payload.data[2])*LOCAL_OUTB.CONF_CAL_CURRENT_COEF_A+LOCAL_OUTB.CONF_CAL_CURRENT_COEF_B)/1000).toFixed(1);
    LOCAL_OUTC.I_AVG = ((((local_msg.payload.data[5]<<8)+local_msg.payload.data[4])*LOCAL_OUTC.CONF_CAL_CURRENT_COEF_A+LOCAL_OUTC.CONF_CAL_CURRENT_COEF_B)/1000).toFixed(1);
    LOCAL_OUTD.I_AVG = ((((local_msg.payload.data[7]<<8)+local_msg.payload.data[6])*LOCAL_OUTD.CONF_CAL_CURRENT_COEF_A+LOCAL_OUTD.CONF_CAL_CURRENT_COEF_B)/1000).toFixed(1);

    global.set('OUT27',LOCAL_OUTA);    //global.set( 'CURRENT_OUT27' , (local_msg.payload.data[0]<<8)+local_msg.payload.data[1]);
    global.set('OUT28',LOCAL_OUTB);    //global.set( 'CURRENT_OUT27' , (local_msg.payload.data[0]<<8)+local_msg.payload.data[1]);
    global.set('OUT29',LOCAL_OUTC);    //global.set( 'CURRENT_OUT27' , (local_msg.payload.data[0]<<8)+local_msg.payload.data[1]);
    global.set('OUT30',LOCAL_OUTD);    //global.set( 'CURRENT_OUT27' , (local_msg.payload.data[0]<<8)+local_msg.payload.data[1]);

    // Save data on the datalogger
    if (LOCAL_OUTA.LOGGING){msg1 = {payload:Number(LOCAL_OUTA.I_AVG)};}
    if (LOCAL_OUTB.LOGGING){msg2 = {payload:Number(LOCAL_OUTB.I_AVG)};}
    if (LOCAL_OUTC.LOGGING){msg3 = {payload:Number(LOCAL_OUTD.I_AVG)};}
    if (LOCAL_OUTD.LOGGING){msg4 = {payload:Number(LOCAL_OUTD.I_AVG)};}

    return [ msg1, msg2, msg3 , msg4 ];
}

