    var LOCAL_OUT37 = RED.util.cloneMessage(global.get('OUT37'));
    var LOCAL_OUT38 = RED.util.cloneMessage(global.get('OUT38'));

    // Extraction of the Data from the CAN FRAME
    // Same temperautre for OUT19-22
    dac= ((local_msg.payload.data[5]<<8)+local_msg.payload.data[4]);
    temperature=(coef_A_OUT19*dac)+coef_B_OUT19;
    temperature=temperature.toFixed(1);
    LOCAL_OUT19.TEMP = temperature;
    LOCAL_OUT20.TEMP = temperature;
    LOCAL_OUT21.TEMP = temperature;
    LOCAL_OUT22.TEMP = temperature;

    // Same temperautre for OUT23-26
    dac= ((local_msg.payload.data[7]<<8)+local_msg.payload.data[6]);
    temperature=(coef_A_OUT23*dac)+coef_B_OUT23;
    temperature=temperature.toFixed(1);
    LOCAL_OUT23.TEMP = temperature;
    LOCAL_OUT24.TEMP = temperature;
    LOCAL_OUT25.TEMP = temperature;
    LOCAL_OUT26.TEMP = temperature;

    // Same temperautre for OUT27-32
    dac= ((local_msg.payload.data[1]<<8)+local_msg.payload.data[0]);
    temperature=(coef_A_OUT27*dac)+coef_B_OUT27;
    temperature=temperature.toFixed(1);
    LOCAL_OUT27.TEMP = temperature;
    LOCAL_OUT28.TEMP = temperature;
    LOCAL_OUT29.TEMP = temperature;
    LOCAL_OUT30.TEMP = temperature;
    LOCAL_OUT31.TEMP = temperature;
    LOCAL_OUT32.TEMP = temperature;

    // Same temperautre for OUT33-38
    dac= ((local_msg.payload.data[3]<<8)+local_msg.payload.data[2]);
    temperature=(coef_A_OUT33*dac)+coef_B_OUT33;
    temperature=temperature.toFixed(1);
    LOCAL_OUT33.TEMP = temperature;
    LOCAL_OUT34.TEMP = temperature;
    LOCAL_OUT35.TEMP = temperature;
    LOCAL_OUT36.TEMP = temperature;
    LOCAL_OUT37.TEMP = temperature;
    LOCAL_OUT38.TEMP = temperature;

    // Save the data
    global.set('OUT19',LOCAL_OUT19);    
    global.set('OUT20',LOCAL_OUT20);    
    global.set('OUT21',LOCAL_OUT21);    
    global.set('OUT22',LOCAL_OUT22);    
    global.set('OUT23',LOCAL_OUT23);    
    global.set('OUT24',LOCAL_OUT24);    
    global.set('OUT25',LOCAL_OUT25);    
    global.set('OUT26',LOCAL_OUT26);    
    global.set('OUT27',LOCAL_OUT27);    
    global.set('OUT28',LOCAL_OUT28);    
    global.set('OUT29',LOCAL_OUT29);    
    global.set('OUT30',LOCAL_OUT30);    
    global.set('OUT31',LOCAL_OUT31);    
    global.set('OUT32',LOCAL_OUT32);    
    global.set('OUT33',LOCAL_OUT33);    
    global.set('OUT34',LOCAL_OUT34);    
    global.set('OUT35',LOCAL_OUT35);    
    global.set('OUT36',LOCAL_OUT36);    
    global.set('OUT37',LOCAL_OUT37);    
    global.set('OUT38',LOCAL_OUT38);    
}

