function HexConv(data)
{
    if(data != null)
    {
        data.canid = "0x" + data.canid.toString(16).toUpperCase();

        for(i = 0; i < data.dlc; i++)
        {
            data.data[i] = data.data[i].toString(16).toUpperCase();

            if ((data.data[i].length % 2) > 0) {
            data.data[i] = "0" + data.data[i];
            }
        }
    }
    return data;
}

