}

else if(msg.payload.canid != 1025) // 0x400 frame, alive frame sent once the product is out of sleep mode
{
    if(flow.get('First')===true)
    {
        flow.set('EndT',msg.payload.timestamp);
        flow.set('Delta', (msg.payload.timestamp - Start.payload));
